import React, {Component} from "react";
import axios from "axios";
import "./EditNews.css";
import {withRouter} from "react-router-dom";

// export default class editNews extends Component {
class editNews extends Component {
    constructor(props) {
        super(props);
        // const { _id, name, status, prize, game, region } =
        //   props.location.state.Tournaments;
        this.state = {
            // _id : "",
            title: "",
            content: "",
        };

        this.editTournament = this.editTournament.bind(this);
        this.handleFieldChange = this.handleFieldChange.bind(this);
    }

    componentDidMount() {
        axios
            // .get("http://localhost:5000/news/api/" + this.state._id)
            // .get("http://localhost:5000/news/api/${this.props.match.params.id}")
            .get("http://localhost:5000/news/api/" + this.props.match.params.id)
            .then((response) => {
                this.setState({
                    // currentTournament: response.data,
                    title: response.data.title,
                    content: response.data.content,
                });
            })
            .catch((error) => {
                console.log(error);
            });
    }

    editNews(event) {
        event.preventDefault();
        const newNews = {
            title: this.state.title,
            content: this.state.content,
        };
        axios
            .put(
                "http://localhost:5000/news/api/" + this.props.match.params.id,
                newNews
            )
            .then((response) => {
                console.log(response);
                window.location.href = "/dashboard/news";
                // alert("news created!");
            })
            .catch((error) => {
                console.log(error);
            });
    }

    handleFieldChange(e) {
        this.setState({
            [e.target.name]: e.target.value,
        });
    }

    render() {
        // const { currentTournament } = this.state;
        return (
            <div>
                <h1 className="titleContent">Edit Tournament</h1>
                <form onSubmit={this.editTournament}>
                    <div className="editTournamentInputGroup">
                        <label>Name</label>
                        <input
                            type="text"
                            name="name"
                            value={this.state.name}
                            onChange={this.handleFieldChange}
                            className="tournamentEditInput"
                        />
                    </div>
                    <div className="editTournamentInputGroup">
                        <label>Status</label>
                        <input
                            type="text"
                            name="status"
                            value={this.state.status}
                            onChange={this.handleFieldChange}
                            className="tournamentEditInput"
                        />
                    </div>
                    <div className="editTournamentInputGroup">
                        <label>Prize</label>
                        <input
                            type="text"
                            name="prize"
                            value={this.state.prize}
                            onChange={this.handleFieldChange}
                            className="tournamentEditInput"
                        />
                    </div>
                    <div className="editTournamentInputGroup">
                        <label>Game</label>
                        <input
                            type="text"
                            name="game"
                            value={this.state.game}
                            onChange={this.handleFieldChange}
                            className="tournamentEditInput"
                        />
                    </div>
                    <div className="editTournamentInputGroup">
                        <label>Region</label>
                        <input
                            type="text"
                            name="region"
                            value={this.state.region}
                            onChange={this.handleFieldChange}
                            className="tournamentEditInput"
                        />
                    </div>
                    <button
                        className="editTournamentButton"
                        type="submit"
                        name="editTournament"
                    >
                        Edit
                    </button>
                </form>
            </div>
        );
    }
}

export default withRouter(editNews);
