        name: this.state.name,
        status: this.state.status,
        prize: this.state.prize,
        game: this.state.game,
        region: this.state.region,
      })
      .then((response) => {
        console.log(response);
        window.location.href = "/dashboard/tournament";
        // alert("tournament created!");
      })
      .catch((error) => {
        console.log(error);
      });
  };

